import { createStore } from 'redux';


const initialState = {
    userUpdated : false
};

const UPDATE_USER = 'UPDATE_USER';

function updateUser(){
	return{
		type : 'USER_UPDATE',
		userUpdated : true
	}
}

const reduc = (state=initialState ,action) => {
  
  switch(action.type){

  	case 'USER_UPDATE':

  	      return Object.assign({},state,{
  	      	userUpdated:action.userUpdated
  	      })
  	default:
  	     return state;      

  }

}

export const store = createStore(reduc);
