import {UPDATE_USER} from './actionTypes'

const initialState = {
    userUpdated : false
};

function toAddUser(state = initialState, action) {

  switch (action.type) {
    case UPDATE_USER:
      return Object.assign({}, state, {
        userUpdated: action.bool
      })
    default:
      return state
  }
}

export default todoUser;
