import {UPDATE_USER,initialState} from './actionTypes'

function todoUser(state=initialState ,action){
  
  switch(action.type){

  	case 'USER_UPDATE':

  	      return Object.assign({},state,{
  	      	userUpdated:action.userUpdated
  	      })
  	default:
  	     return state;      

  }

}
