import React,{Component} from 'react';
import './App.css';
import { connect } from "react-redux";
import editUserData from './actions/index';

function mapDispatchToProps(dispatch,ownProps) {
  return {
    editUserData:() => dispatch(editUserData(ownProps.id)),
    type:'USER_UPDATED'
  }
};

const mapStateToProps = (state) => {
   console.log(state);
  return {
    type: state
  };
};

 export class UserItem extends Component{
	
   constructor(props){
   		super(props);
}

handleOnClick = event => {
    this.props.editUserData();
}

render(){

  return(
          
   <div id='User' key={this.props.id} >
   <div id='header'>
    <label id='name'>{this.props.name.length <= 0 ? 'No Name' : this.props.name }</label>
    <label id='prof'>{this.props.job}</label>
   </div>
   <div id='email'>
    <label>{this.props.email.length <= 0 ? 'No Email' :this.props.email}</label>
   </div>
   <div id='body'>
   <label>Bio...</label>
   <div id='bio_div'>
   <p id='page'>{this.props.bio.length <= 0 ? 'No Bio' : this.props.bio}</p>
   </div>
   </div>
   <button id="btn" onClick={this.handleOnClick}>EDIT</button>
   </div>

  );
  }
  
}


export default connect(mapStateToProps,mapDispatchToProps)(UserItem);
