import React,{Component} from 'react';
import { connect } from "react-redux";
import {UserItem} from './User';
import getUserData from './actions/index';
import {editUserData} from './actions/index';

const mapStateToProps = state => {
  return { users: state.users };
};


export class App extends Component{

constructor(props){
  super(props);
}

componentDidMount(){
   this.props.getUserData();
   this.props.editUserData();
}

render() {

     const datalist = this.props.users.map((item) => (

       <UserItem
         key={item.id}
         id={item.id}
         name={item.name}
         job={item.occupation}
         email={item.email}
         bio={item.bio}
       />

    ));

    return (

     <div id='list_div'>
     {datalist}
     </div>

    );

}

}



export default connect({mapStateToProps},{editUserData})(App);
