import {UPDATE_USER} from './actionTypes'

const reduc = (state=initialState ,action) => {
  
  switch(action.type){

  	case 'USER_UPDATE':

  	      return Object.assign({},state,{
  	      	userUpdated:action.userUpdated
  	      })
  	default:
  	     return state;      

  }

}
