import React,{Component} from 'react';
import './App.css';
import { connect } from "react-redux";
import editUserData from './actions/index';


const mapStateToProps = state => {
  return { users: state.users };
};


export class UserItem extends Component{
	
   constructor(props){
   		super(props);

   }

  handleEdit(evt){
    
    // let  tmp = this.props.id;
    this.props.editUserData(evt);

    console.log(evt);

   }
 
   render(){

     return(
            
     <div id='User' key={this.props.id} >
     <div id='header'>
      <label id='name'>{this.props.name.length <= 0 ? 'No Name' : this.props.name }</label>
      <label id='prof'>{this.props.occupation}</label>
     </div>
     <div id='email'>
      <label>{this.props.email.length <= 0 ? 'No Email' :this.props.email}</label>
     </div>
     <div id='body'>
     <label>Bio...</label>
     <div id='bio_div'>
     <p id='page'>{this.props.bio.length <= 0 ? 'No Bio' : this.props.bio}</p>
     </div>
     </div>
      <button id="btn" onClick={()=> this.handleEdit(this.props.id)} >EDIT</button>
    </div>

     	);

   }
  
}
export default connect(null,{editUserData})(UserItem);
