import {UPDATE_USER,updateUser} from './actionTypes'

const initialState = {
    userUpdated : false
};

function todoUser(state=initialState ,updateUser){
  
  switch(updateUser.type){

  	case 'USER_UPDATE':

  	      return Object.assign({},state,{
  	      	userUpdated:updateUser.userUpdated
  	      })
  	default:
  	     return state;      

  }

}

export default todoUser;
