import {GET_USERS,USER_UPDATED,ERR} from '../constants/action-types';

const initialState = {	
  type:GET_USERS,
  users: [{id:'45544',name:'Felix M',occupation:'Developer',email:'ssample@gmail.com',bio:'It is what it is',created_at:Date.now()}]
};


function rootReducer(state = initialState, action) {

  if (action.type === GET_USERS) {
    return Object.assign({}, state, {
      users: state.users.concat(action.payload)
    });
  }

  if(action.type === USER_UPDATED){
  	return Object.assign({}, state, {
      users: state.users.concat(action.payload)
    });
    
  }

  if(action.type === ERR){
    return ERR;
  }
  return state;
};

export default rootReducer;
