import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';

class AppList extends React.Component{

constructor(props){
  super(props);
  this.users = {data:[]};
}   

componentDidMount() {

	fetch("https://ti-react-test.herokuapp.com/users")
	     .then(res => res.json())
	     .then((response) =>{

	         this.setState({
	           users:response.list
	         });
			})
	     .catch(err =>{
	     	 this.setState({
	     	 	users:[],
	     	 	error:err
	     	 })
	     });
}


render(){

 const userList = this.state.users.map((item) => (

     <App
       key={item.id}
       name={item.name}
       job={item.occupation}
       email={item.email}
       bio={item.bio}

     />
 	));

   return(
      <div>
       {userList}
      </div>


   	);
}
}

ReactDOM.render(<App/>, document.getElementById('root'));
