import {GET_USERS} from '../constants/action-types';

const initialState = {	
  users: [{"id":30,"name":"The UnderTaker",
  "occupation":"Professional Wrestler",
  "email":"undertaker@gmail.com","bio":"I am a professional wrestler and I have an unbeaten streak at wrestlemania",
  "created_at":"2019-07-05T10:22:22.769Z","updated_at":"2020-03-06T07:19:15.406Z"}]
};

function rootReducer(state = initialState, action) {

  if (action.type === GET_USERS) {
    return Object.assign({}, state, {
      users: state.users.concat(action.payload)
    });
  }
  return state;
};

export default rootReducer;
