export class App extends Component{

constructor(props){
  super(props);
  this.state={data:[]};
}

componentDidMount(){

 this.setState({
  data:this.props.users
 });

}
render() {

     const datalist = this.props.users.map((item) => (

     <UserItem
       key={item.id}
       name={item.name}
       job={item.occupation}
       email={item.email}
       bio={item.bio}
     />

    ));

    return (
    <div>
   {datalist}
   </div>
    );

}

}



export default connect(mapStateToProps)(App);
