import {GET_USERS,EDIT_USER} from '../constants/action-types';


export default function getUserData(tmp) {
	return function(dispatch){
		return fetch('https://ti-react-test.herokuapp.com/users')
				.then(res =>res.json())
				.then(json => {
					dispatch({type:GET_USERS,payload:json});
				})
	}
}

export function editUserData(payload) {
	return function(dispatch){
		return fetch("https://ti-react-test.herokuapp.com/users/"+payload,{method:'PATCH',body:JSON.stringify({
           bio:'Civic Techie',
           email:'me@email.com',
           name:'Thenos Hunt',
           occupation:'Hacker'
          }),
      headers:{
        'Content-type':'application/json;charset=UTF-8'
      }
      })
		.then(res =>res.json())
		.then(json => {
			dispatch({type:USER_UPDATED,payload:json});
		})
	}
}
