import {UPDATE_USER} from './actionTypes'

const initialState = {
    userUpdated : false
};

function todoUser(state=initialState ,action){
  console.log(action);
  switch(action.type){

  	case 'USER_UPDATE':

  	      return Object.assign({},state,{
  	      	userUpdated:action.userUpdated
  	      })
  	default:
  	     return state;      

  }

}

export default todoUser;
