import React from "react";
import { connect } from "react-redux";
import './App.css';


const mapStateToProps = state => {
  return { users: state.users };
};

function userClicked(id){
    
  let tmp = id;

  fetch("https://ti-react-test.herokuapp.com/users/"+tmp,{method:'PATCH',body:JSON.stringify({
           
           bio:'Civic Techie',
           email:'me@email.com',
           name:'Thenos Hunt',
           occupation:'Hacker'

          }),
      headers:{
        'Content-type':'application/json;charset=UTF-8'
      }
      })
         .then(res => res.json())
         .then((response) => {
             
             console.log(response);

        })
         .catch(err => {
            
            console.log(err);
             
         });


}

const ConnectedList = ({ users }) => (

    users.map(el => (

    <div id='User' key={el.id} className='User' data-item={el.id}>
     <div id='header'>
      <label id='name'>{el.name.length <= 0 ? 'No Name' : el.name}</label>
      <label id='prof'>{el.occupation}</label>
     </div>
     <div id='email'>
      <label>{el.email.length <= 0 ? 'No Email' : el.email}</label>
     </div>
     <div id='body'>
     <label>Bio...</label>
     <div id='bio_div'>
     <p id='page'>{el.bio.length <= 0 ? 'No Bio' : el.bio}</p>
     </div>
     </div>
      <button id="btn" onClick={ ()=> this.userClicked(el.id)} >PATCH</button>
    </div>
    ))
);

const List = connect(mapStateToProps)(ConnectedList);

export default List;
